查找


function search(root, n) {
  // 若 root 为空，查找失败，直接返回
  if (!root) {
    return
  }
  // 找到目标结点，输出结点对象
  if (root.val === n) {
    console.log('目标结点是：', root)
  } else if (root.val > n) {
    // 当前结点数据域大于n，向左查找
    search(root.left, n)
  } else {
    // 当前结点数据域小于n，向右查找
    search(root.right, n)
  }
}


插入新结点

function insertIntoBST(root, n) {
  // 若 root 为空，说明当前是一个可以插入的空位
  if (!root) {
    // 用一个值为n的结点占据这个空位
    root = new TreeNode(n)
    return root
  }

  if (root.val > n) {
    // 当前结点数据域大于n，向左查找
    root.left = insertIntoBST(root.left, n)
  } else {
    // 当前结点数据域小于n，向右查找
    root.right = insertIntoBST(root.right, n)
  }

  // 返回插入后二叉搜索树的根结点
  return root
