const allDeps = []
let cursor = 0
function useEffect(callback, depsList) {
  // 新的effect直接执行函数
  if (!allDeps[cursor]) {
    allDeps[cursor] = depsList
    ++cursor
    callback()
    return
  }
  const rawDeps = allDeps[cursor]
  // 比较前后2次依赖数组是否有变化
  const isChanged = rawDeps.some((dep, index) => {
    dep !== depsList[index]
  })
  if (isChanged) {
    callback()
    allDeps[cursor] = depsList
  }
  ++cursor
}

