function throttle(fn, time) {
  let timer = null
  return function () {
    if (timer) {
      // 与节流的区别在这里
      return
    }
    timer = setTimeout(() => {
      fn.apply(this, arguments) // 绑定this， 否则this指向window
      timer = null
    }, time || 300)
  }
}

