function myInstanceOf(objA, classA) {
  let __proto = Object.getPrototypeOf(objA)
  const __prototype = classA.prototype
  while (__proto !== null) {
    if (__proto === __prototype) {
      return true
    }
    __proto = Object.getPrototypeOf(__proto)
  }
  return false
}

