发布订阅
class EventEmitter {
  constructor() {
    this.list = {}
  }
  on(event, fn) {
    if (!this.list[event]) {
      this.list[event] = []
    }
    this.list[event].push(fn)
  }
  off(event, fn) {
    const events = this.list[event]
    if(events) {
      this.list[event] = this.list[event].filter((item) => {
        return item !== fn
      })
    }
  }
  emit(event, ...args) {
    const events = this.list[event]
    if(events) {
      events.forEach((item) => {
        item.call(this, ...args)
      })
    }
  }
}

