  // 注意：函数是可以有返回值的
  return result
}

apply
Function.prototype.apply = function (context, arr) {
  context = context ? Object(context) : window
  context.fn = this

  let result
  if (!arr) {
    result = context.fn()
  } else {
    result = context.fn(...arr)
  }

  delete context.fn
  return result
}

